import React,{useState} from "react";
// import api from "../assets/portfolio/api.jpg";
// import apod from "../assets/portfolio/apod.jpg";
// import iptracker from "../assets/portfolio/iptracker.jpg";
// import nftportal from "../assets/portfolio/nftportal.jpg";
// import webpostman from "../assets/portfolio/webpostman.jpg";
// import waveportal from "../assets/portfolio/waveportal.jpg";

const Portfolio = () => {
  const [visibleDetails, setVisibleDetails] = useState(null);
  const portfolios = [
    {
      id: 1,
      src: "https://static.wingify.com/gcp/uploads/sites/3/2021/09/Feature-image-7-ecommerce.png",
      link: 'https://prakashchoure2002.github.io/ecommerce_deploy/',
      repo: 'https://github.com/PrakashChoure2002/ecommerce_deploy',
      details:"reatc"
      
    },
    {
      id: 2,
      src: "https://herobot.app/wp-content/uploads/2022/11/11-Reasons-Why-A-Chat-Application-Is-Great-For-Business_1.jpg",
      link: 'https://webpostman.netlify.app/',
      repo: 'https://github.com/rahulkarda/Web-Postman'
    },
    {
      id: 3,
      src: "https://jupiter.money/blog/wp-content/uploads/2023/09/Overspending-image-jupiter.png",
      link: 'https://prakashchoure2002.github.io/Expance_tracker/',
      repo: 'https://github.com/PrakashChoure2002/Expance_tracker'
    },
    // {
    //   id: 4,
    //   src: waveportal,
    //   link: 'https://waveatrahul.netlify.app/',
    //   repo: 'https://github.com/rahulkarda/Wave-Portal'
    // },
    // {
    //   id: 5,
    //   src: nftportal,
    //   link: 'https://nftportalbyrahul.netlify.app/',
    //   repo: 'https://github.com/rahulkarda/NFT-Portal'
    // },
    // {
    //   id: 6,
    //   src: iptracker,
    //   link: 'https://trackmyip.netlify.app/',
    //   repo: 'https://github.com/rahulkarda/IP-Address-Tracker'
    // },
  ];
  const toggleDetails = (id) => {
    setVisibleDetails(visibleDetails === id ? null : id);
  };

  return (
    <div
      name="portfolio"
      className="bg-gradient-to-b from-black to-gray-800 w-full text-white md:h-screen portfolio"
    >
      <div className="max-w-screen-lg p-4 mx-auto flex flex-col justify-center w-full h-full">
        <div className="pb-8">
          <p className="text-4xl font-bold inline border-b-4 border-gray-500">
            Portfolio
          </p>
          <p className="py-6">Check out some of my work right here</p>
        </div>

        <div className="grid sm:grid-cols-2 md:grid-cols-3 gap-8 px-12 sm:px-0">
          {portfolios.map(({ id, src, link, repo,details }) => (
            <div key={id} className="shadow-md shadow-gray-600 rounded-lg">
              <img
                src={src}
                alt="projects"
                className="rounded-md duration-200 hover:scale-105"
              />
              <div className="flex items-center justify-center">
                <button className="w-1/2 px-6 py-3 m-4 duration-200 hover:scale-105" onClick={ () => window.open(link, '_blank')}>
                  Demo
                </button>
                <button
                  className="w-1/2 px-6 py-3 m-4 duration-200 hover:scale-105"
                  onClick={() => toggleDetails(id)}
                  
                >
                  Details{details}
                </button>
                <button className="w-1/2 px-6 py-3 m-4 duration-200 hover:scale-105" onClick={ () => window.open(repo, '_blank')}>
                  GitHub
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Portfolio;
